<?phpinclude("conexion.php");$conexion = conectardb();//si la variable imagen no ha sido definida nos dara un advertencia.$fotos = $_GET['idfoto'];$hospedaje = $_GET['idhospedaje'];	//vamos a crear nuestra consulta SQL	$consulta = mysqli_query($conexion,"SELECT * FROM imagen WHERE idHospedaje = $hospedaje AND id = $fotos");		$datos = mysqli_fetch_array($consulta);	$idImagen=$datos['id'];    mysqli_query($conexion,"UPDATE imagen SET enMuestra=1 WHERE id=$idImagen");	//ruta va a obtener un valor parecido a "imagenes/nombre_imagen.jpg" por ejemplo	$imagen = $datos['imagen'];	$tipo = $datos['tipo'];	//ahora colocamos la cabeceras correcta segun el tipo de imagen	header("Content-type: $tipo");	echo $imagen;?>